mapper.select:
SELECT r.*
FROM TB_ROLE r,TB_USER u,TB _USER_ROLE t


        AND u.ID = #{id}


        AND u.ACCOUNT = #{account}


AND u.ID = t.USER_ID
AND t.ROLE_ID = R.ID

SELECT rig.* FORM TB_USER use,TB_USER_ROLE ur,TB_ROLE role,TB_ROLE_RIGHT rr,TB_RIGHT rig
AND use.ID = #{id}
AND use.ACCOUNT = #{account}
AND u.ID = t.USER_ID AND t.ROLE_ID = R.ID AND rr.ROLE_ID = R.ID AND rr.RIGHT_ID = rig.ID
